i:1;a:2:{s:5:\"title\";s:5:\"About\";s:4:\"text\";s:735:\"<p>\r\nYou\'re reading <a href=\"http://mark.reid.name\" title=\"Mark Reid\'s Personal Site\">Mark Reid</a>\'s research blog. Mark is a <a href=\"http://users.rsise.anu.edu.au/~mreid/\" title=\"Mark Reid\'s Research Home Page\">research fellow</a> at the <a href=\"http://csl.rsise.anu.edu.au/\">Computer Sciences Lab</a> at the <a href=\"http://anu.edu.au/\" title=\"The ANU\">Australian National University</a>. Naturally, the opinions here are personal ones and do not represent those of the ANU.\r\n</p><p>\r\nYou can find out more about me and this site <a href=\"http://conflate.net/inductio/about/\" title=\"More about this site\">here</a> or browse the <a href=\"http://conflate.net/inductio/archives\" title=\"Older posts\">archive</a> for older posts.\r\n</p>\";}}','yes'),(103,0,'widget_rss','a:3:{s:6:\"number\";i:2;i:220273942;a:6:{s:5:\"title\";s:9:\"Bookmarks\";s:3:\"url\";s:55:\"http://feeds.delicious.com/v2/rss/mreid/machinelearning\";s:5:\"items\";i:5;s:12:\"show_summary\";i:0;s:11:\"show_author\";i:0;s:9:\"show_date\";i:0;}i:220273941;a:6:{s:5:\"title\";s:9:\"CiteULike\";s:3:\"url\";s:40:\"http://www.citeulike.org/rss/user/mdreid\";s:5:\"items\";i:5;s:12:\"show_summary\";i:0;s:11:\"show_author\";i:0;s:9:\"show_date\";i:0;}}','yes'),(104,0,'widget_recent_comments','a:2:{s:5:\"title\";s:0:\"\";s:6:\"number\";i:5;}','yes'),(105,0,'widget_blogtxt_homelink','a:1:{s:5:\"title\";s:0:\"\";}','yes'),(106,0,'widget_blogtxt_rsslinks','a:1:{s:5:\"title\";s:9:\"Subscribe\";}','yes'),(107,0,'widget_blogtxt_recent_comments','a:2:{s:5:\"title\";s:8:\"Comments\";s:7:\"rccount\";s:1:\"5\";}','yes'),(111,0,'cron','a:1:{s:7:\"version\";i:2;}','yes'),(108,0,'wp_hashcash_db','1','yes'),(109,0,'plugin_wp-hashcash-version','a:2:{s:7:\"version\";s:5:\"4.0.5\";s:4:\"last\";i:1202165440;}','yes'),(112,0,'doing_cron','0','yes'),(116,0,'analytics_uastring','UA-1051817-2','yes'),(131,0,'tag_base','','yes'),(134,0,'rss_5523304628798